//thêm vào giỏ hàng
$('.add_tocart').click(function(e){
e.preventDefault();
var id = $(this).parent().find('input[name=id]').val();
var quantity = $('input[name=quantity]').val();
var url = $(this).data('url');

$.ajax({
    type: "POST",
    url: url,
    headers: {'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')},
    data: {'id': id, 'quantity': quantity},
    success: function(data){
        $('.cart-ajax-new').html(data.html1);
        $('.count_item_pr').html(data.html2);
        
    }
})
})



function removeItemCart(n, url) {
    var id = n;
    var url = url;
    $.ajax({
        type: 'get',
        url:url,
        data: {id:id},
        success: function(data) {
            $('.cart-ajax-new').html(data.html1);
            $('.count_item_pr').html(data.html2);
        }
    })
}

function btnMinus(id,url) {
    var id = id;
    var result = document.getElementById('qty'+id); var qtypro = result.value; if( !isNaN( qtypro ) && qtypro > 1 ) result.value--;
    var quantity = result.value;
    var url = url;
    console.log(quantity)
    $.ajax({
        type:'get',
        url:url,
        data: {id:id, quantity:quantity},
        success: function(data) {
            $('.cart-ajax-new').html(data.html1);
        }
    })
}
function btnPlus(id , url) {
    var id = id;
   
    var result = document.getElementById('qty'+id); var qtypro = result.value; if( !isNaN( qtypro )) result.value++;
    var quantity = result.value;
    console.log(quantity);
    var url = url;
    $.ajax({
        type:'get',
        url:url,
        data: {id:id, quantity:quantity},
        success: function(data) {
            $('.cart-ajax-new').html(data.html1);
        }
    })
}

