      <img class="scroll-top" src="{{asset('frontend/imgs/crollup.png   ')}}" style="display:none;cursor :pointer; position : fixed;bottom : 40px;right : 33px;z-index : 999;" />
      <script>
         $(document).ready(function () {
             $(window).scroll(function () {
                 if ($(window).scrollTop() > 700) {
                     $('.scroll-top').show();
                 }
                 else {
                     $('.scroll-top').hide();
                 }
             });
         
             $('.scroll-top').click(function () {
                 $("html, body").animate({ scrollTop: 0 }, "slow");
             });
             if ($(window).width() < 768) {
                 $('.scroll-top').css({ 'bottom': '40px', 'right': '8px' });
             }
         });
      </script>
